import react, { useState } from "react";
import AddIcon from '@mui/icons-material/Add';
import Fab from '@mui/material/Fab';
import Zoom from '@mui/material/Zoom';

function CreateArea(props){

    const [note, setNote] = useState({
        title: "",
        content: ""
    });

    function handleChanged(event){

        const {name, value} = event.target;

        setNote(prevNote => {
            return {
                ...prevNote,
                [name]: value
            };
        });
    }

    function onSubmitted(event){
        event.preventDefault();
        props.onAdd(note);
        setNote({
            title: "",
            content:""
        });
    }

    return(
        <form className="create-note">
            <input type="text" name="title" value={note.title} onChange={handleChanged} placeholder="Title" />
            <textarea name="content" value={note.content} onChange={handleChanged} placeholder="Take a note..." rows="3"></textarea>
            <Zoom>
                
            </Zoom>
            <Fab onClick={onSubmitted}>
                <AddIcon />
            </Fab>
        </form>
    )
}

export default CreateArea;
