import react, { useState } from "react";

function CreateArea(){

    const [note, setNote] = useState({
        title: "",
        content: ""
    });

    function handleChanged(event){

        const {name, value} = event.target;
        setNote(prevValue => {
            return {
                ...prevValue,
                [name]: value
            };
        })
    }

    return(
        <form>
            <input type="text" name="title" value="title" onChange={handleChanged} placeholder="Title" />
            <textarea name="note" value="" onChange={handleChanged} placeholder="Take a note..." rows="3"></textarea>
            <button type="submit">Add</button>
        </form>
    )
}

export default CreateArea;
